import React, { useRef } from 'react';

const Checklist = () => {
  const checklistRef = useRef([]);

  const handleCheckboxChange = (index) => {
    const updatedChecklist = [...checklistRef.current];
    updatedChecklist[index] = !updatedChecklist[index];
    checklistRef.current = updatedChecklist;
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log('Checklist:', checklistRef.current);
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <label>
        <input
          type="checkbox"
          checked={checklistRef.current[0]}
          onChange={() => handleCheckboxChange(0)}
        />
        Item 1
      </label>
      <label>
        <input
          type="checkbox"
          checked={checklistRef.current[1]}
          onChange={() => handleCheckboxChange(1)}
        />
        Item 2
      </label>
      <label>
        <input
          type="checkbox"
          checked={checklistRef.current[2]}
          onChange={() => handleCheckboxChange(2)}
        />
        Item 3
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Checklist;
