import React, { useRef } from 'react';

const Toggle = () => {
  const [toggleRef, setToggleRef] = useRef(false);

  const handleToggle = () => {
    setToggleRef(()=>!toggleRef) //true, false 반전
    console.log(toggleRef); 
  };

  return (
    <div>
      <button onClick={handleToggle}>
        Button
      </button>
    </div>
  );
};

export default Toggle;
