import React, { useRef } from 'react';

const Carousel = () => {
  const carouselRef = useRef(null);
  const currentIndexRef = useRef(0);

  const handleNextSlide = () => {
    currentIndexRef.current = (currentIndexRef.current + 1) % carouselRef.current.children.length;
    carouselRef.current.style.transform = `translateX(-${currentIndexRef.current * 100}%)`;
  };

  const handlePrevSlide = () => {
    currentIndexRef.current = (currentIndexRef.current - 1 + carouselRef.current.children.length) % carouselRef.current.children.length;
    carouselRef.current.style.transform = `translateX(-${currentIndexRef.current * 100}%)`;
  };

  return (
    <div className="carousel-container">
      <div className="carousel" ref={carouselRef}>
        <div className="slide">Slide 1</div>
        <div className="slide">Slide 2</div>
        <div className="slide">Slide 3</div>
      </div>
      <button className="prev-button" onClick={handlePrevSlide}>Prev</button>
      <button className="next-button" onClick={handleNextSlide}>Next</button>
    </div>
  );
};

export default Carousel;
