import React , {useState} from 'react';

function Radio(){
    const [nowSelect, setNowSelect] = useState(null);

    const onSelect = (e) => {
        console.log(e.target.name);
        setNowSelect(e.target.name);
    }
    return (
        <div>
            <h1>{nowSelect}</h1>
            <label>
                <input type="radio" onChange={onSelect} name="option1"/> 1번
            </label>
            <label>
                <input type="radio" onChange={onSelect} name="option2"/> 2번
            </label>


        </div>
    )
}

export default Radio;
